import { Component } from '@angular/core';
import { Reporte1 } from 'src/app/Clases/Reporte1';
import { ReporteService } from 'src/app/servicios/Administrador/reporte.service';

@Component({
  selector: 'app-reporte2',
  templateUrl: './reporte2.component.html',
  styleUrls: ['./../../inventcomponent/informacion/informacion.component.css']
})
export class Reporte2Component {

  constructor(private servicio:ReporteService){}
  tipo:number
  reporte1:Reporte1[];

  public GenerarReporte(){
    console.log(this.tipo);
    this.servicio.getReporte().subscribe(data =>{
      this.reporte1 = data;
      console.log(this.reporte1);
    })

  }


}
<body>
    <div class="col-md-6 offset-md-3">
        <form (submit)="GenerarReporte()" name="formulario">
            <h1>Reporte</h1>
            <div class="form-group">
                <select>
                    <option selected="true" disabled="disabled">Sucursal</option>
                    <option>Central</option>
                    <option>Norte</option>
                    <option>Sur</option>
                    <option>General</option>
                </select>
                <i class='bx bx-store'></i>
            </div>
            <div class="form-group">
                <select [(ngModel)]="tipo" name="tipo">
                    <option selected="true" disabled="disabled">Tipo de Reporte</option>
                    <option value=1>Clientes que mas han gastado</option>
                    <option value=2>Descuentos realizados</option>
                    <option value=3>10 ventas mas grandes</option>
                    <option value=4>10 articulos mas vendido</option>
                    <option value=5>10 ventas mas grandes</option>
                    <option value=6>sucursales con mas ingresos</option>
                </select>
                <i class='bx bxs-report' ></i>
            </div>
            <button type="submit" class="btn">Generar Reporte</button>
        </form>
        </div>
</body>
