import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Producto } from 'src/app/Clases/Producto';

@Injectable({
  providedIn: 'root'
})
export class VentaService {

  constructor(private http:HttpClient) { }
  path = 'http://localhost:8080/Cajero';

  public AgregarDetalle(codigo:String,Cantidad:number,Detalle:DProducto[]):void{
    let tmp:DProducto = new DProducto("AB",codigo,Cantidad);
    this.ObtenerTotal("AB").subscribe(data =>{
      tmp.Total = Cantidad * data;
    })
    Detalle.push(tmp);
  }



  public ObtenerTotal(codigo:String){
    return this.http.post<number>(this.path,codigo)
  }

}



class DProducto{
  Factura:String;
  codigo:String;
  Cantidad:number;
  Total:number;
  constructor(Factura:String,codigo:String,cantida:number){
    this.Factura = Factura;
    this.codigo = codigo;
    this.Cantidad = cantida;
}


}
