import { Component, OnInit, numberAttribute } from '@angular/core';
import { NgForm } from '@angular/forms';
import { BodegaService } from 'src/app/servicios/Bodega/bodega.service';

@Component({
  selector: 'app-bodega',
  templateUrl: './bodega.component.html',
  styleUrls: ['./../../login/login.component.css']
})
export class BodegaComponent implements OnInit{

  constructor(private servicio:BodegaService){}

  ngOnInit(): void {

  }

  public GuardarProducto(form:NgForm){
    let id:String = form.value.id;
    let nombre:String = form.value.nombre;
    let marca:String = form.value.marca;
    let precio:number = form.value.precio;
    let pasillo:String = form.value.pasillo;
    this.servicio.IngresarProducto(id,nombre,marca,precio,pasillo);
  }

}
