import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Producto } from 'src/app/Clases/Producto';
import { TarjetaService } from 'src/app/servicios/Administrador/tarjeta.service';
import { VentaService } from 'src/app/servicios/Cajero/venta.service';

@Component({
  selector: 'app-venta',
  templateUrl: './venta.component.html',
  styleUrls: ['./venta.component.css']
})
export class VentaComponent implements OnInit{
  Detalle:any[];
  Clientes:String[]
  ListaProducto:Producto[]
  Codigo:String

  Total = {valor:0};

  constructor(private servicio:VentaService,private servicio2:TarjetaService){}
  ngOnInit(): void {
    this.Detalle = [];

    this.servicio.getProductos("3").subscribe(data=>{
      this.ListaProducto = data;
      this.ListaProducto.forEach(element => {
        console.log(element.nombre);
      });
    })

    this.servicio2.getClientes(this.Clientes).subscribe(data =>{
      this.Clientes = data;
    })

  }

  public Agregar(form:NgForm){
    let Cantidad:number = form.value.cantidad;

    console.log('Codigo ' +  this.Codigo);

    if(this.Codigo.length != 0 && Cantidad > 0){
      this.servicio.AgregarDetalle(this.Codigo,Cantidad,this.Detalle,this.Total);
    }
    else{
      {{alert('Ingrese todos los campos')}}
    }
  }

  public eliminarFila(I:number){
    console.log('Eliminando');
    this.Detalle.splice(I,1);
    this.servicio.ActualizarTotal(this.Detalle);
  }

}
