import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class VentaService {

  constructor(private http:HttpClient) { }
  path = 'http://localhost:8080/user';

  public AgregarDetalle(Factura:String,codigo:String,Cantidad:Number,Detalle:Producto[]):void{
    let tmp:Producto = new Producto(Factura,codigo,Cantidad);
    /*LOgica para agregar el total */
    Detalle.push(tmp);
  }

  public ObtenerTotal(codigo:String):Number{
    this.http.post<Number>(this.path,5).subscribe(todo=>{
      console.log(todo);
    })
    return 5;
  }

}

class Producto{
  Factura:String;
  codigo:String;
  Cantidad:Number;
  Total:Number;
  constructor(Factura:String,codigo:String,cantida:Number){
    this.Factura = Factura;
    this.codigo = codigo;
    this.Cantidad = cantida;
}


}
