import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { TarjetaService } from 'src/app/servicios/Administrador/tarjeta.service';
import { VentaService } from 'src/app/servicios/Cajero/venta.service';

@Component({
  selector: 'app-venta',
  templateUrl: './venta.component.html',
  styleUrls: ['./venta.component.css']
})
export class VentaComponent implements OnInit{
  Detalle:any[];
  Clientes:String[]
  Total = {valor:0};

  constructor(private servicio:VentaService,private servicio2:TarjetaService){}
  ngOnInit(): void {
    this.Detalle = [];
    this.servicio2.getClientes(this.Clientes).subscribe(data =>{
      this.Clientes = data;
    })
  }

  public Agregar(form:NgForm){
    let Codigo:String = form.value.codigo;
    let Cantidad:number = form.value.cantidad;

    if(Codigo.length != 0 && Cantidad > 0){
      this.servicio.AgregarDetalle(Codigo,Cantidad,this.Detalle,this.Total);

      console.log("TOTAL DESPUES: " + this.Total);

    }
    else{
      {{alert('Ingrese todos los campos')}}
    }
  }

}
