import { Component, OnInit } from '@angular/core';
import { CookieService } from 'ngx-cookie-service';
import { Empleado } from 'src/app/Clases/Empleado';
import { Producto } from 'src/app/Clases/Producto';
import { InventarioService } from 'src/app/servicios/Inventario/inventario.service';

@Component({
  selector: 'app-informacion',
  templateUrl: './informacion.component.html',
  styleUrls: ['./informacion.component.css']
})
export class InformacionComponent implements OnInit{

  constructor(private servicio:InventarioService,private cookie:CookieService){}

  informacion:Producto

  codigo:String
  empleado:Empleado = JSON.parse(this.cookie.get('Empleado'))

  ngOnInit(): void {
    this.informacion = new Producto()
    console.log(this.empleado);

  }

  public getProducto(id:String){
    console.log('codigo' + id);

    this.servicio.getProducto(id,this.empleado.sucursal).subscribe(data =>{
      if(data != null){
        this.informacion = data
      }
      else alert('El producto no existe en la sucursal')

    })
  }
}

<div class="col-md-6 offset-md-3">
    <form (submit)="getProducto(codigo)">
        <div class="form-group">
            <h1>Codigo</h1>
            <input type="text" class="form-control" name="codigo" [(ngModel)]="codigo"  #id="ngModel" required>
        </div>
        <button type="submit" class="btn" [disabled]="id.invalid">Informacion</button>
    </form>

<div>
    <table class="table table-striped">
        <thead class="table-dark">
        <tr>
            <th>Id</th>
            <th>Nombre</th>
            <th>Marca</th>
            <th>Precio</th>
            <th>Pasillo</th>
            <th>Bodega</th>
            <th>Estantes</th>
        </tr>
        </thead>
        <tbody class="table-dark">
        <tr >
            <td>{{informacion.id}}</td>
            <td>{{informacion.nombre}}</td>
            <td>{{informacion.marca}}</td>
            <td>{{informacion.precio}}</td>
            <td>{{informacion.pasillo}}</td>
            <td></td>
            <td></td>
        </tr>  
        </tbody>
    </table>    
</div>
</div>
