import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class BodegaService {
  constructor(private http:HttpClient) { }
  path = 'http://localhost:8080/bodega';

  public IngresarProducto(Id:String,Nombre:String, Marca:String,Precio:number,Pasillo:number){
    let tmp:Producto = new Producto(Id,Nombre,Marca,Precio,Pasillo);


    this.http.post<any>(this.path,tmp).subscribe(data=>{
      console.log(data);

    });
    this.http.get<Producto>(this.path).subscribe(data=>{
      console.log(data);

    });

  }
}

class Producto{

   Id: String;
   Nombre: String;
   Marca:String;
   Sucursal:String;
   Precio: number;
   Pasillo: String;
   constructor(Id:String,Nombre:String, Marca:String,Precio:number,Pasillo:String){
    this.Id = Id;
    this.Nombre = Nombre; 
    this.Marca = Marca;
    this.Precio = Precio;
    this.Pasillo = Pasillo;
}
}
