import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { NgForm } from "@angular/forms";
import { Observable, map } from "rxjs";

@Injectable({
    providedIn: 'root'
})

export class loginservice{
    private API="http://localhost:8080/user";

    constructor(private httpCliente: HttpClient){

    }

    public IniciarSesion(Formulario:NgForm){
        const usuario = Formulario.value.usuario;
        const password = Formulario.value.password;
        let User:Usuario = new Usuario(usuario,password);
        let User2:Observable<Usuario> = this.httpCliente.post<Usuario>(`${this.API}`,User);
        console.log('Se inicio la sesion');
    }

}

class Usuario{
    private Nombre:String;
    private Contrasena:String;
    private Tipo:number;
    private Empleado:number;
    private Caja:number;

    constructor(Nombre:String,Contrasena:String){
        this.Contrasena = Contrasena;
        this.Nombre = Nombre;
    }

    imprimir(){
        console.log(this.Contrasena);

    }

}
