import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Producto } from 'src/app/Clases/Producto';

@Injectable({
  providedIn: 'root'
})
export class VentaService {

  constructor(private http:HttpClient) { }
  path = 'http://localhost:8080/Cajero';

  async AgregarDetalle(codigo:String,Cantidad:number,Detalle:DProducto[],obj: {TotalGeneral:number}){
    let tmp:DProducto = new DProducto("AB",codigo,Cantidad);
    this.ObtenerTotal(codigo).subscribe(data =>{
      tmp.Total = Cantidad * data;
      TotalGeneral = this.ActualizarTotal(Detalle);
      console.log('nuevo total: ' + TotalGeneral );

    })
    Detalle.push(tmp);
  }

  public ActualizarTotal(Detalle:DProducto[]):number{
    let Total:number = 0;
    for (let index = 0; index < Detalle.length; index++) {
      let tmp:DProducto = Detalle[index];
      Total+= tmp.Total;
    }
    return Total;
  }

  public ObtenerTotal(codigo:String){
    return this.http.post<number>(this.path,codigo)
  }

}



class DProducto{
  Factura:String;
  codigo:String;
  Cantidad:number;
  Total:number;
  constructor(Factura:String,codigo:String,cantida:number){
    this.Factura = Factura;
    this.codigo = codigo;
    this.Cantidad = cantida;
}


}
