import { HttpClient, HttpStatusCode } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class BodegaService {
  constructor(private http:HttpClient) { }
  path = 'http://localhost:8080/bodega';

  public IngresarProducto(Id:String,Nombre:String, Marca:String,Precio:number,Pasillo:String){
    let tmp:Producto = new Producto(Id,Nombre,Marca,Precio,Pasillo);
    tmp.sucursal = '3';

    this.http.post<HttpStatusCode>(this.path,tmp).subscribe(data=>{
      console.log(data);

    });


  }
}

class Producto{

   id: String;
   nombre: String;
   marca:String;
   sucursal:String;
   precio: number;
   pasillo: String;
   constructor(Id:String,Nombre:String, Marca:String,Precio:number,Pasillo:String){
    this.id = Id;
    this.nombre = Nombre; 
    this.marca = Marca;
    this.precio = Precio;
    this.pasillo = Pasillo;
}
}
