import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Producto } from 'src/app/Clases/Producto';

@Injectable({
  providedIn: 'root'
})
export class VentaService {

  constructor(private http:HttpClient) { }
  path = 'http://localhost:8080/Cajero';

  public AgregarDetalle(Factura:String,codigo:String,Cantidad:Number,Detalle:DProducto[]):void{
    let tmp:DProducto = new DProducto(Factura,codigo,Cantidad);
    tmp.Total = this.ObtenerTotal("AB");
    /*LOgica para agregar el total */
    Detalle.push(tmp);
  }

  public ObtenerTotal(codigo:String):Number{
    let Precio:Number = 0;

    this.http.post<any>(this.path,codigo).subscribe(data=>{
       Precio = data;
       console.log(data);
    })
    console.log(Precio);

    return Precio;
  }

}



class DProducto{
  Factura:String;
  codigo:String;
  Cantidad:Number;
  Total:Number;
  constructor(Factura:String,codigo:String,cantida:Number){
    this.Factura = Factura;
    this.codigo = codigo;
    this.Cantidad = cantida;
}


}
