import { Component } from '@angular/core';
import { CerrarService } from 'src/app/servicios/cerrar.service';

@Component({
  selector: 'app-cajero',
  templateUrl: './cajero.component.html',
  styleUrls: ['./cajero.component.css']
})
export class CajeroComponent {
  constructor(private servicio:CerrarService){}

  public CerrarSesion(){
    this.servicio.cerrarSesion();
  }

}
<body>    
    <nav>
        <ul>
            <li [routerLink]="['/caja/venta']"> <a><h1>Venta</h1></a></li>
            <li [routerLink]="['/caja/cliente']"> <a><h1>Registro Cliente</h1></a></li>
            <li [routerLink]="['/caja/Modificar']"> <a><h1>Modificar Cliente</h1></a></li>
            <li>
                <button class="input-box" (click)="CerrarSesion()"><h1>Cerrar Sesion</h1> </button>
            </li>
        </ul>
    </nav>
</body>
<router-outlet></router-outlet>
